import React from "react";
import { Users } from "lucide-react";

const UserSelect = ({ users, selectedUserId, onUserSelect }) => {
  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h2 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
        <Users size={20} />
        Select User
      </h2>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">
          Choose a user to claim points for
        </label>
        <select
          value={selectedUserId}
          onChange={(e) => onUserSelect(e.target.value)}
          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          size={1} // ensures it’s dropdown style
          onFocus={(e) => e.target.size = 6}  // show 6 items on focus
          onBlur={(e) => e.target.size = 1}   // revert on blur
        >
          <option value="">Choose a user...</option>
          {users.map((user) => (
            <option key={user._id} value={user._id}>
              {user.name} ({user.totalPoints} pts)
            </option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default UserSelect;
