import React, { useState, useEffect } from 'react';
import AddUser from './components/AddUser';
import UserSelect from './components/UserSelect';
import ClaimButton from './components/ClaimButton';
import Leaderboard from './components/Leaderboard';
import { fetchUsers } from './services/api';

const App = () => {
  const [users, setUsers] = useState([]);
  const [selectedUserId, setSelectedUserId] = useState('');
  const [loading, setLoading] = useState(false);

  const loadUsers = async () => {
    setLoading(true);
    const data = await fetchUsers();
    setUsers(data);
    setLoading(false);
  };

  useEffect(() => {
    loadUsers();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-6xl mx-auto">
        <div className="grid md:grid-cols-3 gap-6">
          <AddUser onUserAdded={loadUsers} loading={loading} />
          <UserSelect users={users} selectedUserId={selectedUserId} setSelectedUserId={setSelectedUserId} />
          <ClaimButton selectedUserId={selectedUserId} onClaimed={loadUsers} loading={loading} />
        </div>
        <Leaderboard users={users} />
      </div>
    </div>
  );
};

export default App;
