import React from 'react';
import { Star } from 'lucide-react';
import { claimPoints } from '../services/api';

const ClaimButton = ({ selectedUserId, onClaimed, loading }) => {
  const handleClaim = async () => {
    if (!selectedUserId) return;
    await claimPoints(selectedUserId);
    onClaimed();
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6 w-full">
      <h2 className="text-lg font-semibold text-gray-800 mb-4 flex items-center gap-2">
        <Star size={20} /> Claim Points
      </h2>
      <button
        onClick={handleClaim}
        disabled={loading || !selectedUserId}
        className="w-full bg-green-500 hover:bg-green-600 text-white py-3 px-4 rounded-md font-semibold disabled:opacity-50"
      >
        {loading ? 'Claiming...' : 'ðŸŽ² Claim Random Points (1-10)'}
      </button>
    </div>
  );
};

export default ClaimButton;
