import React from 'react';
import { Users } from 'lucide-react';

const Leaderboard = ({ users }) => {
  const sorted = [...users].sort((a, b) => b.totalPoints - a.totalPoints);

  const getRankColor = (rank) =>
    rank === 1 ? 'text-yellow-500' :
    rank === 2 ? 'text-gray-400' :
    rank === 3 ? 'text-orange-500' :
    'text-gray-600';

  const getRankSuffix = (rank) => {
    const j = rank % 10, k = rank % 100;
    if (j === 1 && k !== 11) return 'st';
    if (j === 2 && k !== 12) return 'nd';
    if (j === 3 && k !== 13) return 'rd';
    return 'th';
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6 mt-6">
      <h2 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
        <Users size={20} /> Leaderboard
      </h2>
      {sorted.map((user, index) => {
        const rank = index + 1;
        return (
          <div key={user._id} className="flex justify-between items-center p-3 border rounded-lg mb-2">
            <div className="flex gap-4 items-center">
              <div className={`text-xl font-bold ${getRankColor(rank)}`}>
                {rank}<sup>{getRankSuffix(rank)}</sup>
              </div>
              <div>
                <h3 className="text-lg font-semibold">{user.name}</h3>
                <p className="text-sm text-gray-600">{user.totalPoints} points</p>
              </div>
            </div>
            <span className="text-sm text-gray-500">Rank #{rank}</span>
          </div>
        );
      })}
    </div>
  );
};

export default Leaderboard;
