import React from "react";
import { Users } from "lucide-react";
import Select from 'react-select';

const UserSelect = ({ users, selectedUserId, onUserSelect }) => {
  const options = users.map(user => ({
    value: user._id,
    label: `${user.name} (${user.totalPoints} pts)`,
  }));

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h2 className="text-xl font-semibold text-gray-800 mb-4 flex items-center gap-2">
        <Users size={20} />
        Select User
      </h2>

      <label className="block text-sm font-medium text-gray-700 mb-2">
        Choose a user to claim points for
      </label>

      <Select
        options={options}
        value={options.find(opt => opt.value === selectedUserId)}
        onChange={(selected) => onUserSelect(selected.value)}
        styles={{
          menu: (provided) => ({ ...provided, maxHeight: 200 }),
        }}
      />
    </div>
  );
};

export default UserSelect;
